import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Navigation from './components/Navigation';
import TodoCrudForm from './components/TodoCrudForm';

import {todos} from './todos.json';

class App extends Component {
  constructor() {
    super();
    this.state = {
      todos
    };
  this.handleAddTodo = this.handleAddTodo.bind(this);
  this.removeTodo = this.removeTodo.bind(this);
  }

  handleAddTodo(todo) {
    this.setState ({
      todos: [...this.state.todos, todo]
    })
  }

  removeTodo(index) {
    this.setState ({
      todos: this.state.todos.filter((e, i) => {
        return i !== index
      })
    })
  }

  render() {
  const todos = this.state.todos.map((todo, i)=>{
    const clickHandler = () => this.removeTodo.bind(this, i);

    return(
      <div className="col-md-4">
        <div className="card mt-4">
          <div className="card-header">
            <h3> {todo.title} </h3>
            <span className="badge badge-pill badge-danger ml-2">
              {todo.priority}
            </span>
          </div>
          <div className="card-body">
            <p> {todo.description} </p>
            <p> {todo.responsible} </p>
          </div>
          <div className="card-footer">
            <button className="btn btn-danger" onClick={clickHandler}>
              Delete
            </button>
          </div>
        </div>
      </div>
    )
  })

    return (
      <div className="App">

        <Navigation title="Task" numberTask={todos}/>
        <img src={logo} className="App-logo" alt="logo" />
        <div className="container">
          <div className="row mt-4">
            <div className="col-md-3">

              <TodoCrudForm onAddTodo={this.handleAddTodo}/>
            </div>
            <div className="col-md-9">
              <div className="row">
                {todos}
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
